mapper.select:
SELECT
notify.id,notify.sender_id AS senderId,notify.content,notify.type,notify.target_id AS targetId,
notify.target_type AS targetType,notify.action,notify.create_time AS createTime,
user_notify.id AS userNotifyId, user_notify.is_read As isRead
FROM
notify
LEFT JOIN user_notify ON notify.id = user_notify.notify_id
WHERE
notify.sender_id <> #{userId}
AND user_notify.user_id = #{userId}

    AND user_notify.is_read = #{isRead}


    AND notify.target_type = #{targetType}

ORDER BY notify.create_time DESC
