mapper.select:
select count(${column}) as total

    sum( case when ${column}= #{columnValue} then 1 else 0 end) as ${columnValue}

from ${table}
where 1=1

    and (${table}.${timeName} BETWEEN #{startTime} AND #{endTime})

select count(${column}) as total,

    sum( case when ${column}= #{columnValue} then 1 else 0 end) as ${columnValue}

from ${table}
where 1=1

    and (${table}.${timeName} BETWEEN #{startTime} AND #{endTime})
